import React from "react";
import { useNavigate ,useParams} from 'react-router-dom';
import './category.styles.css';
import Base from "../layout/Base.Component";
import { useEffect,useState } from "react";
import Baseurl from "../../components/baseurl/Baseurl.component";

export default function Showcategory(props){
    const navigate =useNavigate();
    const [inputs,setInputs] = useState({});

    const {http,noapi_http} = Baseurl();
    const {id}= useParams();
    

    const fetchcategory=()=>{
        http.get('/category/show/'+id).then((res)=>{
            setInputs({
                name:res.data.name,
                image:res.data.image
            })
            
        });
    }
    useEffect(()=>{
        fetchcategory();
     },[])

    
    return(
        <Base>
                
          <section className="p-3">
             <div className="card">
                <h3>Category Detail</h3>
                <div className="form">
                    
                   <div className="row">
                     <div className="col-lg-6">
                     <div className="form-group">
                        <strong>Category Name:</strong>
                        <p>{inputs.name}</p>
                     </div>
                     </div>
                     <div className="col-lg-6">
                     <div className="form-group">
                        <strong>Image:</strong>
                        <p>{inputs.image}</p>
                        <img src={noapi_http+inputs.image}/>
                     </div>
                     </div>
                   </div>
                     
                  
                   <br/>
                  
                   
                   
                </div>
             </div>
          </section>
          
        </Base>
    );
}
