import React from "react";
import Header from "./Header.component";
import Footer from "./Footer.component";
import Sidebar from "./Sidebar.component";
import './layout.styles.css';
const Base=({children})=>(
    <div className="base">
        
         <Header/>
         <Sidebar/>
         <div className="children">
         {children}
         </div>
         <Footer/>
    </div>
);
export default Base;
